import './App.css';
import axios from 'axios'
import { useState, useEffect } from 'react'

function App() {


  const [pokemons, setPokemons] = useState([])

  const getPokemon = async () => {

    const result = await axios.get('http://localhost:3001/api/pokemon');

    if (result.status !== 200) {
      return
    }
    const list = result.data.body.results
    setPokemons(list)
  }

  useEffect(() => {
    getPokemon();
  }, [])



  return (
    <div className="container relative z-40 mx-auto mt-12">
      <div class="flex flex-wrap justify-center mx-auto lg:w-full md:w-5/6 xl:shadow-small-blue">
        {
          pokemons.sort((a, b) => a.name > b.name ? 1 : -1).map((pokemon, index) => {
            return (
              <div className='block w-1/2 py-10 text-center border lg:w-1/4' key={index}>
                <p className='pt-4 text-sm font-medium capitalize font-body text-green-900 lg:text-lg md:text-base md:pt-6' key={index}>{pokemon.name}</p>
              </div>
            )
          })
        }
      </div>
    </div>
  );
}

export default App;
