import './App.css';
import axios from 'axios'
import { useState, useEffect } from 'react'

function App() {


  const [pokemons, setPokemons] = useState([])

  const getPokemon = async () => {

    const result = await axios.get('http://localhost:3001/api/pokemon');

    if (result.status !== 200) {
      return
    }
    const list = result.data.body.results
    setPokemons(list)
  }

  useEffect(() => {
    getPokemon();
  }, [])

  return (
    <body>
      <div >
        <div className="mx-auto max-w-6xl">
          <div id="features" className="mx-auto max-w-6xl">
            <h2 className="text-center font-display text-3xl font-bold tracking-tight text-slate-900 md:text-4xl">
              Lista de Pokemons
            </h2>
            <ul className="mt-16 grid grid-cols-1 gap-6 text-center text-slate-700 md:grid-cols-3">
              {
                pokemons.sort((a, b) => a.name > b.name ? 1 : -1).map((pokemon, index) => {
                  return (
                    <li className="rounded-xl bg-white px-6 py-8 shadow-sm">
                      <h2 className="my-3 font-display font-medium">{pokemon.name}</h2>
                      <button><p className='text-xs'>Ver detalles</p></button>
                    </li>
                  )
                })
              }
            </ul>
          </div>
        </div>
        <div class="flex justify-center items-center">
          <button class="rounded-full border border-slate-300 py-2 px-3 text-center text-sm transition-all shadow-sm hover:shadow-lg text-slate-600 hover:text-white hover:bg-slate-800 hover:border-slate-800 focus:text-white focus:bg-slate-800 focus:border-slate-800 active:border-slate-800 active:text-white active:bg-slate-800 disabled:pointer-events-none disabled:opacity-50 disabled:shadow-none ml-2">
            Prev
          </button>
          <button class="min-w-9 rounded-full bg-slate-800 py-2 px-3.5 border border-transparent text-center text-sm text-white transition-all shadow-md hover:shadow-lg focus:bg-slate-700 focus:shadow-none active:bg-slate-700 hover:bg-slate-700 active:shadow-none disabled:pointer-events-none disabled:opacity-50 disabled:shadow-none ml-2">
            1
          </button>
          <button class="min-w-9 rounded-full border border-slate-300 py-2 px-3.5 text-center text-sm transition-all shadow-sm hover:shadow-lg text-slate-600 hover:text-white hover:bg-slate-800 hover:border-slate-800 focus:text-white focus:bg-slate-800 focus:border-slate-800 active:border-slate-800 active:text-white active:bg-slate-800 disabled:pointer-events-none disabled:opacity-50 disabled:shadow-none ml-2">
            2
          </button>
          <button class="min-w-9 rounded-full border border-slate-300 py-2 px-3.5 text-center text-sm transition-all shadow-sm hover:shadow-lg text-slate-600 hover:text-white hover:bg-slate-800 hover:border-slate-800 focus:text-white focus:bg-slate-800 focus:border-slate-800 active:border-slate-800 active:text-white active:bg-slate-800 disabled:pointer-events-none disabled:opacity-50 disabled:shadow-none ml-2">
            3
          </button>
          <button class="min-w-9 rounded-full border border-slate-300 py-2 px-3 text-center text-sm transition-all shadow-sm hover:shadow-lg text-slate-600 hover:text-white hover:bg-slate-800 hover:border-slate-800 focus:text-white focus:bg-slate-800 focus:border-slate-800 active:border-slate-800 active:text-white active:bg-slate-800 disabled:pointer-events-none disabled:opacity-50 disabled:shadow-none ml-2">
            Next
          </button>
        </div>
      </div>
    </body>

  );
}

export default App;
