import './App.css';
import axios from 'axios'
import { useState, useEffect } from 'react'

function App() {


  const [pokemons, setPokemons] = useState([])

  const getPokemon = async () => {

    const result = await axios.get('http://localhost:3001/api/pokemon');

    if (result.status !== 200) {
      return
    }
    const list = result.data.body.results
    setPokemons(list)
  }

  useEffect(() => {
    getPokemon();
  }, [])



  return (
    <section class="py-12 bg-gray-900 text-gray-100 sm:py-12 lg:py-16">
      <div className="grid grid-cols-4 gap-4">
        {
          pokemons.sort((a, b) => a.name > b.name ? 1 : -1).map((pokemon, index) => {
            return (
              <div key={index}>
                <p key={index}>{pokemon.name}</p>
              </div>
            )
          })
        }
      </div>
    </section>
  );
}

export default App;
