'use client'
import { useParams } from 'react-router-dom'
import { usePokemonStore } from '../../../store/pokemon/pokemon-store'
import { useEffect, useState } from 'react'
import Spinner from '../../../component/ui/Spinner'

export const Pokemon = () => {

    const { nombre } = useParams()

    const { pokemon, getPokemonByName, isLoading } = usePokemonStore()
    useEffect(() => {

        getPokemonByName(nombre || '')

    }, [])

    console.log(pokemon)

    return (
        <div className="h-screen">
            {

                isLoading || pokemon == null
                    ?
                    <Spinner />
                    :
                    <>
                    <img className='h-fit' src={pokemon.body.sprites.back_default} />
                    <img className='h-fit' src={pokemon.body.sprites.front_default} />

                    </>
                    
                    
            }
        </div>
    )
}
