import './App.css';
import axios from 'axios'
import { useState, useEffect } from 'react'

function App() {


  const [pokemons, setPokemons] = useState([])

  const getPokemon = async () => {

    const result = await axios.get('http://localhost:3001/api/pokemon');

    if (result.status !== 200) {
      return
    }
    const list = result.data.body.results
    setPokemons(list)
  }

  useEffect(() => {
    getPokemon();
  }, [])



  return (
    <div class="bg-gray-200 px-2 py-10">
      <div className="mx-auto max-w-6xl">

        <div id="features" class="mx-auto max-w-6xl">
          <h2 class="text-center font-display text-3xl font-bold tracking-tight text-slate-900 md:text-4xl">
            Lista de Pokemons
          </h2>
          <ul class="mt-16 grid grid-cols-1 gap-6 text-center text-slate-700 md:grid-cols-3">
            {
              pokemons.sort((a, b) => a.name > b.name ? 1 : -1).map((pokemon, index) => {
                return (
                  <li class="rounded-xl bg-white px-6 py-8 shadow-sm">
                    <h3 class="my-3 font-display font-medium">{pokemon.name}</h3>
                    <button>Ver detalles</button>
                  </li>
                )
              })
            }
          </ul>
        </div>
      </div>
    </div>
  );
}

export default App;
