import axios from 'axios';
import { create } from 'zustand'

interface State {
    isLoading: boolean;
    pokemon: Array<any>;
    pokemons: Array<any>
    getPokemon: () => void;
    getPokemonByName: (nombre: string) => void;
    getPokemonByLimitPage: (limit: number, offset: number) => void;
}

export const usePokemonStore = create<State>((set) => ({

    isLoading: true,
    pokemon: [],
    pokemons: [],
    getPokemon: async () => {
        set({ isLoading: false })
        const result = await axios.get('http://localhost:3001/api/pokemon');
        if (result.status != 200) {
            set({ isLoading: false, pokemons: [] })
            return
        }
        set({ isLoading: false, pokemons: result.data.body.results })
    },
    getPokemonByName: async (nombre) => {
        const result = await axios.get(`http://localhost:3001/api/pokemon/${name}`);
        if (result.status != 200) {
            set({ isLoading: false, pokemon: [] })
            return
        }
        set({ isLoading: false, pokemon: result.data })

    },
    getPokemonByLimitPage: async (limit, offset) => {

    }
}))
