'use client'
import { useParams } from 'react-router-dom'
import { usePokemonStore } from '../../../store/pokemon/pokemon-store'
import { useEffect, useState } from 'react'
import Spinner from '../../../component/ui/Spinner'

export const Pokemon = () => {

    const { nombre } = useParams()

    const { pokemon, getPokemonByName, isLoading } = usePokemonStore()
    useEffect(() => {

        getPokemonByName(nombre || '')

    }, [])

    return (
        <div className="h-screen fade-in m-9">
            {

                isLoading || pokemon == null
                    ?
                    <Spinner />
                    :
                    <>
                        <button type="button"
                            className="py-2.5 px-5 me-2 mb-2 text-sm font-medium text-gray-900 focus:outline-none bg-white rounded-lg border border-gray-200 hover:bg-gray-100 hover:text-blue-700 focus:z-10 focus:ring-4 focus:ring-gray-100 dark:focus:ring-gray-700 dark:bg-gray-800 dark:text-gray-400 dark:border-gray-600 dark:hover:text-white dark:hover:bg-gray-700">
                            <a href='/'>Volver</a>
                        </button>

                        <a href="#" className="block max-w-sm p-6 bg-white border border-gray-200 rounded-lg shadow hover:bg-gray-100 dark:bg-gray-800 dark:border-gray-700 dark:hover:bg-gray-700">

                            <h5 className="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">{pokemon.body.name}</h5>
                            <div><img className='h-fit' src={pokemon.body.sprites.front_default} /></div>
                            <div><img className='h-fit' src={pokemon.body.sprites.back_default} /></div>
                            <h5 className="mb-2 text-2xl font-bold tracking-tight text-gray-900 dark:text-white">Tipo:</h5>
                            <p className="font-normal text-gray-700 dark:text-gray-400">{pokemon.body.types[0].type.name}</p>
                            <p>Habilidades:</p> {pokemon.body.stats.map((habilidad: any) => {
                                return (
                                    <p>
                                        {habilidad.stat.name}
                                    </p>
                                )
                            })}
                        </a>
                    </>
            }
        </div>
    )
}
